from fractions import Fraction

# 積三百步
積步數 = 300

# 置積步數，以十二乘之
積乘十二 = 12 * 積步數

# 以開方除之，即得周
# 開方即為平方根，手動計算平方根
def 開方(數):
    # 手動開平方根的近似方法（牛頓法）
    x = Fraction(數)
    guess = x / 2
    for _ in range(10):  # 迭代10次以提高精度
        guess = (guess + x / guess) / 2
    return guess

周 = 開方(積乘十二)

a = 周


